mapper.update:
update user
username = #{username,jdbcType=CHAR}, `password` = #{password,jdbcType=VARCHAR}, salt = #{salt,jdbcType=VARCHAR}, `name` = #{name,jdbcType=VARCHAR}, phone = #{phone,jdbcType=VARCHAR}, email = #{email,jdbcType=VARCHAR}, enabled = #{enabled,jdbcType=BIT}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, username, `password`, salt, `name`, phone, email, enabled
from user
where id = #{id,jdbcType=INTEGER}

select id, username, `password`, salt, `name`, phone, email, enabled
from user

    id=#{id}
    and username=#{username}
    and password=#{password}
